  svg
    .append('rect')
    .attr('class', 'timeBar')
    .attr('y', firstBarY+90)
    .attr('width', d => scaleM(Number(barMils)+Number(barBeats)/100+Number(barMb)/100000))
  svg
    .append('rect')
    .attr('class', 'timeBarFull')
    .attr('y', firstBarY+90)
