    .append('rect')
    .attr('class', 'tickM1')
    .attr('x', d=>scaleM(d)+barX)
    .attr('y', firstBarY+92.5)
    .attr('height', 2.5)
  svg.selectAll('.tickLabel1')
    .data(d3.range(width > 500 ? 1 : 2, 10))
