  svg
    .append('rect')
    .attr('class', 'timeBarFull')
    .attr('y', firstBarY+120)
    .attr('width', d => scaleCandB(barBeats))
  // Beat ticks
  svg.selectAll('.tickB')
    .data(d3.range(width > 500 ? 10 : 20, 100))
    .enter()
    .append('rect')
